import React from 'react';
import { Link } from 'react-router-dom';
import ShowImage from './ShowImage';
import Moment from 'react-moment';
import moment from 'moment';
import Product from './Product';
const Card = ({product,viewProductButton = true}) => {
    const showProductButton = () => {
        return(
            <Link to = {`/product/${product._id}`}  className = "pr-3">
            {
                viewProductButton && (
                    <button className = "btn btn-outline-primary  mt-2 mb-2">View Product</button>
                )
            }
            
        </Link>
        );
    }
    const showQuentity = (quentity) =>
    {
        return(
            quentity > 0 ? (<span className = 'badge badge-primary badge-pill'>In stock</span>) : (<span className = 'badge badge-primary badge-pill'>In stock</span>)
        );
    }
    return(
        
        <div className = "card">
            <div className = "card-header">{product.name}</div>
            <div className = "card-body">
                <ShowImage item = {product} url = "products"/>
                <p className = "black-10">{product.description}</p>
                <p className = "black-9">Cartegory: {product.category && product.category.name}</p>
                <p className = "black-8">${product.price}</p>
                <p className = "black-7">
                    Add on <Moment fromNow>{product.createdAt}</Moment>
                </p>
                
                <br></br>
                {showProductButton()}
                {showQuentity}
                <button className = "btn btn-outline-warning mt-2 mb-2">Add to cart</button>
            </div>
        </div>
    );
}
export default Card;
