const authentication = (data,next) =>
{
    if(typeof window != 'undefined')
    {
        localStorage.setItem('jwt',JSON.stringify(data));
        next();
    }
};
const isAuthenticate = (data,next) =>
{
    if(typeof window != 'undefined')
    {
        localStorage.setItem('jwt',JSON.stringify(data));
        next();
    }
};
export default authentication ;
export  {isAuthenticate} ;
