import React,{useState} from 'react';
import Layout from '../core/Layout';
const AddCategory =  () => 
{
const[name,setName] = useState('')
const[error,setError] = useState(false);
const[success,setSuccess] = useState(false);
const creatUi = () =>
{
    return(

    <form className = "form-group">
    <div>
    <label className ="text-muted">Name</label>
    <br></br>
    <input type="text" className = "form-control" autoFocus value = {name} onChange = {onChangeHandler}/>
    <br></br>
    <button className = "btn btn-outline-primary" onClick={onClickHandler} >Submit</button>
    </div>
    </form>
    );
}
const onChangeHandler  = (e) => {
    setError('')
    setName(e.target.value)
}
const AddNewCategory = (name) => {
    return(
        fetch("http://localhost:8000/api/category/creat/:userId", {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(name)
    })
    .then(Response => {
        return Response.json();
    })
    .catch(error => {
        return error;
    })
    );
} 
const onClickHandler = (e) => 
{
    e.preventDefault();
    AddNewCategory(name);
}
    return(
        <Layout title = "Create Category" discreption = "This page for creating categories" className ="container col-md-8 offset-md-2">
        {creatUi()}
        {JSON.stringify(name)}
        
        </Layout>
    );
}
export default AddCategory ;
