import React,{useState,useEffect} from 'react';
import Layout from '../core/Layout';
import { isAuthenticate } from '../user/auth';
import { AddNewProduct } from './apiAdmin';
import { Link } from 'react-router-dom';
const AddProduct = () => {
    const {user,token} = isAuthenticate()
    const [values,setValues] = useState({
        name: '',
        description : '',
        price : '',
        category : '',
        categories : '',
        Quentity: '',
        photo: '',
        shipping : '', 
        loading : '', 
        createdProduct : '',
        redirectToProfile: '',
        formData: ''
    }); 
    const {
        name,
        description,
        price ,
        category ,
        categories,
        Quentity,
        shipping , 
        loading , 
        createdProduct ,
        redirectToProfile,
        formData
    } = values
    return (
        <Layout title = 'Add New Product '  discreption = "Are you ready to add new product ? "  className ="container col-md-8 offset-md-2">
            <form className = "mb-3">
                <h3>Upload Photo</h3>
                <div className="form-group">
                    <label className ="text-muted">Upload Photo</label>
                </div>
            </form>
        </Layout>
    );
}
export default AddProduct;
