import React from 'react';
import { Redirect, Route, useLocation } from 'react-router-dom';
import { isAuthenticate } from '../user/auth';
const PrivateRoute = ({ children, ...rest })=> {
    let location = useLocation();
    return (
        <Route
          {...rest}
          render={({ location }) =>
            isAuthenticate() ? (
              children
            ) : (
              <Redirect
                to={{
                  pathname: "/login",
                  state: { from: location }
                }}
              />
            )
          }
        />
      );
}
export default PrivateRoute;
