import React from 'react';
import { Link } from 'react-router-dom';

const Card = ({product}) => {
    return(
        <div className = "col-4 mb-3">
        <div className = "card">
            <div className = "card-header">{product.name}</div>
            <div className = "card-body">
                <p>{product.description}</p>
                <p>{product.price}</p>
                <Link to = "/">
                    <button className = "btn btn-outline-primary mx-auto">View Product</button>
                </Link>
                <button className = "btn btn-outline-warning mx-auto">Add to cart</button>
            </div>
        </div>
        </div>
    );
}
export default Card;
