import { useState, useEffect } from 'react';
import { getCategory,list } from './apiCore';
import Card from './Card';

const Search = () => {
    const [data,setData] = useState({
        categories : [],
        category:'',
        search: '',
        results : [],
        searched : false 

    });
    const {categories,category,search ,results ,searched } = data; 
    const loadcategories = () => 
    {
        getCategory().then(data => {
            if(data.error)
            {
                console.log(data.error)
            }
            else
            {
                setData({...data,categories:data,searched:false})
            }
        })
    }
    useEffect(() => {
        loadcategories();
    },[]) 
    const onSubmitHandler = (e) => 
    {
        e.preventDefault();
        searchData();
        
        
    }
    const searchData = () => {
        list({category:category,Search:search}).then(data => {
            if(data.error)
            {
                console.log(data.error)
            }
            else
            {
                setData({...data,results:data,searched : true})
            }
        })
    }
    const onHandelChange = name => event => 
    {
        setData({...data,[name] : event.target.value})
        
    }
    const SearchForm = () => (
        <form onSubmit ={onSubmitHandler}>
            <span className = 'input-group-text'>
                <div className ='input-group '>
                    <div className = "input-group-prepend mr-2 mt-2 ml-2 mb-2">
                        <select className = 'btn mr-2' onChange = {onHandelChange('category')}>
                            <option value = "All">Pick Category</option>
                            {categories.map((c,i) => (
                                <option key = {i} value = {c._id} >{c.name}</option>
                            ))}
                        </select>
                    </div>
                        <input type ='search' className = 'form-control mt-2 mr-2 ml-2' placeholder = "Search by name" onChange = {onHandelChange('name')}></input>
                </div>
                <div className = 'btn input-group-append ' style ={{border:'none'}}>
                    <button className = 'input-group-text'>
                        Search
                    </button>
                </div>
            </span>
        </form>
    )
    return(
        <div className = 'row ml-2 mr-2'>
            <div className = "container"> {SearchForm()}</div>
            <div className = 'container-fluid'> 
            </div>
        </div>
    ); 

}
export default Search
