import { useState, useEffect } from 'react';
import { getCategory } from './apiCore';

const Search = () => {
    const [data,setData] = useState({
        categories : [],
        category:'',
        search: '',
        results : [],
        searched : false 

    });
    const {categories,category,search ,results ,searched } = data; 
    const loadcategories = () => 
    {
        getCategory().then(data => {
            if(data.error)
            {
                console.log(data.error)
            }
            else
            {
                setData({...data,categories:data})
            }
        })
    }
    useEffect(() => {
        loadcategories();
    },[]) 
    const SearchForm = () => (
        <form>
            <span className = 'input-group-text'>
                <div className ='input-group input-group-lg'>
                    <div className = "input-group-prepend mr-2 mt-2 ml-2 mb-2">
                        <select className = 'btn mr-2'>
                            <option value = "All">Pick Category</option>
                            {categories.map((c,i) => (
                                <option key = {i} value = {c._id} >{c.name}</option>
                            ))}
                        </select>
                    </div>
                        <input type ='search' className = 'form-control mt-2 mr-2 ml-2' placeholder = "Search by name"></input>
                </div>
                <div className = 'btn input-group-prepend ' style ={{border:'none'}}>
                    <button className = 'input-group-text'>
                        Search
                    </button>
                </div>
            </span>
        </form>
    )
    return(
        <div className = 'row mb-3'>
            <div className = "container"> {SearchForm()}</div>
        </div>
    ); 

}
export default Search
