import Layout from './Layout';
import { useState, useEffect } from 'react';
import { getItems } from './CartHelper';
import Card from './Card';
const Cart = () => {
    const [items,setItems] = useState([{}]);
    useEffect(() => {
      setItems(getItems())   
    },[])
    const showProductsInCart = () => {
        return(
            items.map((p,i) => (
                <Card key = {i} Product ={p}></Card>
            ) )
        );
    }
    return(
        <Layout title ="Cart Page" discreption = "Welcome to your Cart" className = "container-fluid">
            {showProductsInCart()}
            {JSON.stringify(items)}
    </Layout>
    );

}
export default Cart;
