import React from 'react';
import { Link } from 'react-router-dom';
import { withRouter } from 'react-router';
import { createBrowserHistory } from "history";
const isActive = (path) =>
{
    const history = createBrowserHistory();
   return  history.location.pathname === path ? {color : '#ff9900'} : {color : '#fffffff'} ;
}
const menu = () =>{ 
    return(
        <div>
            <ul className = "nav nav-tabs bg-primary">
                <li className = "nav-item">
                    <Link className = "nav-link" to ="/" style = {isActive("/")}>Home</Link>
                </li>
                <li className = "nav-item">
                    <Link className = "nav-link" to ="/signup" style =  {isActive("/signup")}>Signup</Link>
                </li>
                <li className = "nav-item">
                    <Link className = "nav-link" to ="/signin" style =  {isActive("/signin")}>Signin</Link>
                </li>
            </ul>
        </div>
    );

}
export  default withRouter(menu) ;
