import Layout from './../core/Layout';
import { useState } from 'react';
import { isAuthenticate } from './auth';
import { readProfile } from './apiUser';
import { useEffect } from 'react';
const Profile = () => {
    const [values,setValues] = useState({
        name:'',
        email:'',
        password:'',
        error:'',
        success: false
    })
    const {name,email,password,error,success} = values
    const {user,token} = isAuthenticate();
    const loadProfile = () =>{
        readProfile(user._id,token)
        .then(data => {
            if(data.error)
            {
                console.log(data.error)
                setValues({...values,error:data.error})
            }
            else{
                setValues({...values,
                    name:data.name,
                    email:data.email,
                    password:data.password,
                })
            }

        })
    }
    useEffect(() => {
        loadProfile();
    },[])
    const onChangeHandler = name => e => {
        setValues({...values,[name]:e.target.value})
    }
    return(
        <Layout title ="My Profile" discreption = {`Welcome ${values.name}`} className = "container-fluid">
            <h2>Profile update</h2>
            <form>
                <div className = 'form-group'>
                    <label className = "text-muted">Name</label>
                    <input className = "form-control mb-3" value = {values.name} onChange ={onChangeHandler('name')}></input>
                </div>
                <div className = 'form-group'>
                    <label className = "text-muted">E-mail</label>
                    <input type = "email" className = "form-control" value = {email} onChange ={onChangeHandler('email')}></input>
                </div>
                <div className = 'form-group'>
                    <label className = "text-muted">Password</label>
                    <input type = "password" className = "form-control" value = {password} onChange ={onChangeHandler('password')}></input>
                </div>
            </form>
            {JSON.stringify(values)}
        </Layout>
        );
}
export default Profile;
