import Layout from './Layout';
import { useState, useEffect } from 'react';
import { getItems } from './CartHelper';
import Card from './Card';
const Cart = () => {
    const [items,setItems] = useState([{}]);
    useEffect(() => {
      setItems(getItems())   
    },[])
    const showProductsInCart = () => {
        return(
            <div className = 'col-4 mr-3'>
                <h2>
                    Related Products
                </h2>
                {items.map((P,i) => (
                    <div key = {i} className = 'mr-3 mb-3'>
                    <Card  product = {P}></Card>
                    </div>
                ))}
                
            </div>
        );
    }
    return(
        <Layout title ="Cart Page" discreption = "Welcome to your Cart" className = "container-fluid">
            {showProductsInCart()}
    </Layout>
    );

}
export default Cart;
